package main

import "fmt"

func Solve1(instruction string) int {
	ans := 0
	for i := 0; i < len(instruction); i++ {
		if instruction[i] == "("[0] {
			ans += 1
		} else if instruction[i] == ")"[0] {
			ans -= 1
		}
	}
	return ans
}

func Solve2(instruction string) int {
	pos := 0
	ans := 0
	for i := 0; i < len(instruction); i++ {
		if instruction[i] == "("[0] {
			pos += 1
		} else if instruction[i] == ")"[0] {
			pos -= 1
		}
		ans += 1
		if pos < 0 {
			break
		}
	}
	return ans
}

